function validate_ck_editor_response(str) {

    str = str.replaceAll("&nbsp;", "");
    str = str.replaceAll("<p>", "");
    str = str.replaceAll("</p>", "");
    str = str.replaceAll("<br>", "");
    str = str.replaceAll("<br />", "");
    str = str.replaceAll("&bull;", "");

    str = str.trim();
    return str;
}
