                for(let i = 0; i < verify_inputs.length; i++){
                    verify_inputs[i].classList.remove('forgot-pass-otp-input-error')
                    verify_inputs[i].classList.add('forgot-pass-otp-input-success')
                    verify_inputs[i].value = ''
                }
