function format_time(seconds) {
    var minutes, hours, time = '';
    if (seconds > 60) {
        minutes = parseInt(seconds / 60);
        seconds = seconds - (minutes * 60);

        if (minutes > 60) {
            hours = parseInt(minutes / 60);
            minutes = minutes - (hours * 60);

            time = hours + ' hr ';
        }

        time += minutes + ' min ';
    }

    time += seconds + ' sec ';

    return time;
}

